/* Create FieldOfStudy table with both language and country IDs. Since this is a fairly exhaustive list of fields, we will attempt to reuse it should future use cases require it */
CREATE TABLE dbo.FieldOfStudy
(FieldOfStudyID int NOT NULL IDENTITY(1,1),
FieldOfStudyName varchar(50) NOT NULL,
CCCTOPCode int,
LanguageID int,
CountryID int,
CreatedDate datetime,
UpdatedDate datetime,
ModifiedBy varchar(10),
PRIMARY KEY (FieldOfStudyID),
FOREIGN KEY (LanguageID, CountryID) REFERENCES language(LanguageID, CountryID))

