INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (1,'Multiple Choice','Ask people to choose one answer from your list of answer choices.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (2,'Checkboxes','Let people select multiple answers from your list of answer choices.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (3,'Image Choice','Ask people to choose one or more image answers from your list of image answer choices.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (4,'Dropdown','Provide a dropdown list of answer choices and ask people to choose one answer. This is a great way to save space if you have a long list of options, like country, state, or province.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (5,'Star Rating','Ask respondents to evaluate a statement on a visual scale of stars, hearts, thumbs, or smilies.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (6,'Matrix/Rating Scale','Use a Matrix/Rating Scale question if you want respondents to evaluate one or more row items using the same set of column choices. You can set the matrix question to collect multiple answers or only one answer per row. You also have the option to turn the matrix question into a rating scale when you want to assign weights to each answer choice.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (7,'File Upload','Allow respondents to attach a file to their response.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (8,'Slider','Ask respondents to rate an item or statement on a numerical scale by dragging an interactive slider.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (9,'Matrix of Dropdown Menus','Ask respondents to evaluate several items using the same set of measurements. They can choose an answer from a preset list of answer choices presented in a dropdown menu.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (10,'Ranking','Ask respondents to rank a list of options in the order they prefer using numeric dropdown menus or drag-and-drop.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (11,'Net Promoter® Score','Ask your customers the Net Promoter® Score question to measure customer satisfaction with your organization, brand, product or service. We''ll calculate your score automatically when you analyze your results.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (12,'Single Textbox','Add a single textbox to your survey when you want respondents to write in a short text or numerical answer to your question. You can also specify answer length, and require and validate a number value, date format, or email address.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (13,'Multiple Textboxes','Collect multiple short text or numerical answers with a single question. You can also specify answer length, and require and validate a number value, date format, or email address.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (14,'Comment Box','Use the comment or essay box to collect open-ended, written feedback from respondents.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (15,'Contact Information','Use this question type to collect a respondent''s relevant contact information, including name, address, email, and phone number.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (16,'Date/Time','Ask respondents to enter a specific date and/or time.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (17,'Text','Insert additional text to your survey design to provide extra context or description to your respondents.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (18,'Image','Insert an image into your survey by uploading an image file or specifying the URL of an image on the web.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (19,'Text A/B Test','Learn how respondents react to two or more different text passages. Enter the text variables you want to compare, then specify the percentage of respondents who see each message.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (20,'Image A/B Test','Learn how respondents react to two or more different images. Provide the images to compare, then specify the percentage of respondents who see each one.', '2018-05-25', '2018-05-25', 'jd')
INSERT INTO questionType (questionTypeID, name, description, createdDate, updatedDate, modifiedBy) VALUES (21,'Matrix of Sliders','Ask respondents to rate an item or statement on a numerical scale by dragging an interactive slider.', '2018-05-25', '2018-05-25', 'jd')
